//canvas
//https://cr-vue.mio3io.com/examples/canvas.html#%E3%82%BD%E3%83%BC%E3%82%B9%E3%82%B3%E3%83%BC%E3%83%89
export default {
  name: "PaintPallet",
  el: tmp,
  props: {
    title: String,
    color: String
  },
  data: function () {
    return {
      is_drawing_mode: false,
      mouse_x: null,
      mouse_y: null,
      canvas_x: null,
      canvas_y: null
    };
  },
  mounted: function () {
    this.canvas = this.$ref.canvas;
    this.rect = this.canvas.getBoundingClientsRect(); //https://note.affi-sapo-sv.com/js-canvas-click-coordinate.php
  },

  methods: {
    getMousePosition(e) {
      this.mouse_x = e.clientX;
      this.mouse_y = e.clientY;
    },
    getCanvasPosition() {
      this.canvas_x = this.mouse_x;
      console.log(typeof this.canvas, typeof this.rect);
    },
    draw(e) {
      switch (this.is_drawing_mode) {
        case false:
          break;
        case true:
          {
            this.getMousePosition(e);
            this.getCanvasPosition();
            break;
          }
        default:
          break;
      }
    },
    changeMode() {
      this.is_drawing_mode = !this.is_drawing_mode;
    }
  }
};
