//canvas
//https://cr-vue.mio3io.com/examples/canvas.html#%E3%82%BD%E3%83%BC%E3%82%B9%E3%82%B3%E3%83%BC%E3%83%89
export default {
  name: "PaintPallet",
  props: {
    title: String,
    color: String
  },
  data: function () {
    return {
      is_drawing_mode: false,
      window_x: null,
      window_y: null,
      canvas_x: null,
      canvas_y: null
    };
  },
  mounted: function () {
    this.canvas = this.$refs.canvas;
    this.context = this.canvas.getContext("2d");
  },
  methods: {
    draw(e) {
      switch (this.is_drawing_mode) {
        case false:
          break;
        case true:
          {
            this.rect = this.canvas.getBoundingClientRect(); //https://note.affi-sapo-sv.com/js-canvas-click-coordinate.php
            this.window_x = e.clientX;
            this.window_y = e.clientY;
            //this.context.fillRect(this.window_x - this.rect.left, this.window_y - this.rect.top,1,1);
            this.context.fillRect(e.offsetX - this.rect.left, e.offsetY - this.rect.top, 1, 1);
            console.log(e.offsetX, this.rect.left, e.offsetY, this.rect.top);
            break;
          }
        default:
          break;
      }
    },
    changeMode() {
      this.is_drawing_mode = !this.is_drawing_mode;
    }
  }
};
